mapper.insert:
INSERT INTO REPLY(
no
, rno
, content
, writer
)
VALUES( #{no}
, REPLY_SEQ.NEXTVAL
, #{content}
, #{writer} )

mapper.update:
UPDATE reply SET content = #{content}
writer = #{writer}
where rno = #{rno}

mapper.select:
SELECT no, rno, content, writer, writeDate
FROM reply
where rno = #{rno}
